import React from 'react';
import { Card, Button } from "react-bootstrap";
import ClientDetails from './ClientDetails';

const ClientCard = ({client}) => {

    return (
        <>
             <Card style={{ width: '18rem',height: '150px' }}>
      <Card.Img variant="" src={client.Logo} />
      <Card.Body>
        <Card.Title >{client.CompanyName}</Card.Title>
        <Card.Text>
          {client.CompanyAdresse}
          </Card.Text>
          <Card.Text>
          {client.Phone}
        </Card.Text>
        <Button  variant="primary"><Link to={<ClientDetails/>}>Details</Link></Button>
        <Button  variant="primary">Message</Button>
      </Card.Body>
    </Card>
    </>
    );
}

export default ClientCard;
