import Form from 'react-bootstrap/Form';
import { Row,Col, Container } from 'react-bootstrap';
import "./FormClients.css"
import { useState } from 'react';
import { useDispatch,useSelector } from "react-redux";

const FormClients = () => {
  const client = useSelector(state => state.client)
  console.log(client)
  // const [client, setClient] = useState({
  //   CompanyName:"",
  //   CompanyStreet:"",
  //   CompanyStreet2:"",
  //   CompanyCity:"",
  //   CompanyState:"",
  //   CompanyZip:"",
  //   Gsttreatment:"",
  //   Vat:"",
  //   Phone:"",
  //   Mobile:"",
  //   Email:"",
  //   WebsiteLink:"",
  //   Tags:"",

  // });
  // const HandleCompanyName = (e)=> setClient({
  //   ...client,
  //   CompanyName: e.target.value
  // });
  // const HandleCompanyStreet = (e)=> setClient({
  //   ...client,
  //   CompanyStreet: e.target.value
  // });
  // const HandleCompanyStreet2 = (e)=> setClient({
  //   ...client,
  //   CompanyStreet2: e.target.value
  // });
  // const HandleCompanyCity = (e)=> setClient({
  //   ...client,
  //   CompanyCity: e.target.value
  // });
  // const HandleCompanyState = (e)=> setClient({
  //   ...client,
  //   CompanyState: e.target.value
  // });
  // const HandleCompanyCountry = (e)=> setClient({
  //   ...client,
  //   CompanyCountry: e.target.value
  // });
  
  // const HandleCompanyZip = (e)=> setClient({
  //   ...client,
  //   CompanyZip: e.target.value
  // });
  // const HandleGsttreatment = (e)=> setClient({
  //   ...client,
  //   Gsttreatment: e.target.value
  // });
  // const HandleVat = (e)=> setClient({
  //   ...client,
  //   Vat: e.target.value
  // });
  // const HandlePhone = (e)=> setClient({
  //   ...client,
  //   Phone: e.target.value
  // });
  // const HandleMobile = (e)=> setClient({
  //   ...client,
  //   Mobile: e.target.value
  // });
  // const HandleEmail = (e)=> setClient({
  //   ...client,
  //   Email: e.target.value
  // });
  // const HandleWebsiteLink = (e)=> setClient({
  //   ...client,
  //   WebsiteLink: e.target.value
  // });
  // const HandleTags = (e)=> setClient({
  //   ...client,
  //   Tags: e.target.value
  // });

  // console.log(client);
  

  return (
    <>
    <Container fluid className='backgroundd'>
      <Row className='mt-5 pt-5'>
        <Col lg={5}>
        <Form.Control size="lg" type="text" placeholder="Name" id="CompanyName" onChange={HandleCompanyName} />
        </Col>
      </Row>
    <Row className='my-5'>
      <Col lg={5}>
        <Row className='mb-2'>
          <Col lg={{span: 4}}>
            <Form.Label htmlFor="Company Adresse">Company Adresse:</Form.Label>
            </Col>
          <Col >
            <Form.Control size="sm" type="text" placeholder="Street"  id="Company Adresse" onChange={HandleCompanyStreet} />
            </Col>
            </Row>
        <Row className='mb-2'>
          <Col lg={{ span: 8, offset: 4 }}>
            <Form.Control size="sm" type="text" placeholder="Street2.." id="Company Street" onChange={HandleCompanyStreet2} />
            </Col>
            </Row>
        <Row className='mb-2'>
          <Col lg={{ span: 8, offset: 4 }}>
          <Row className='mb-2'>
            <Col>
              <Form.Control size="sm" type="text" placeholder="City" id="Company City" onChange={HandleCompanyCity} />
              </Col>
            <Col>
              <Form.Control size="sm" type="text" placeholder="State" id="Company State" onChange={HandleCompanyState} />
              </Col>
            <Col>
              <Form.Control size="sm" type="text" placeholder="ZIP" id="Company ZIP" onChange={HandleCompanyZip} />
              </Col>
              </Row>
            </Col>
          <Col lg={{ span: 8, offset: 4 }}>
            <Form.Control size="sm" type="text" placeholder="Country" onChange={HandleCompanyCountry} />
            </Col>
          </Row>
        <Row className='mb-2'>
          <Col lg={{span: 4}}><Form.Label htmlFor="GST treatment">GST treatment:</Form.Label></Col>
          <Col><Form.Control size="sm" type="text" id='GST treatment' onChange={HandleGsttreatment}/></Col>
        </Row>
        <Row className='mb-2'>
          <Col lg={{span: 4}}><Form.Label htmlFor="VAT">VAT:</Form.Label></Col>
          <Col><Form.Control size="sm" type="text" placeholder="e.g BE3546843468" id='VAT' onChange={HandleVat} /></Col>
        </Row> 
      </Col>
      <Col lg={6}>
        <Row className='mb-2'>
          <Col lg={{offset: 2}}><Form.Label htmlFor="Phone">Phone:</Form.Label></Col>
          <Col lg={{span: 8}}><Form.Control size="sm" type="text" id="Phone" onChange={HandlePhone} /></Col>
        </Row>
        <Row className='mb-2'>
          <Col lg={{offset: 2}}><Form.Label htmlFor="Mobile">Mobile:</Form.Label></Col>
          <Col lg={{span: 8}}><Form.Control size="sm" type="text" id="Mobile" onChange={HandleMobile} /></Col>
        </Row>
        <Row className='mb-2'>
          <Col lg={{offset: 2}}><Form.Label htmlFor="Email">Email:</Form.Label></Col>
          <Col lg={{span: 8}}><Form.Control size="sm" type="text"id="Email" onChange={HandleEmail} /></Col>
        </Row>
        <Row className='mb-2'>
          <Col lg={{offset: 2}}><Form.Label htmlFor="Website Link">Website Link:</Form.Label></Col>
          <Col lg={{span: 8}}><Form.Control size="sm" type="text" placeholder="e.g https //www.google.com" id="Website Link" onChange={HandleWebsiteLink} /></Col>
        </Row>
        <Row className='mb-2'>
          <Col lg={{offset: 2 }}><Form.Label htmlFor="Tags">Tags:</Form.Label></Col>
          <Col lg={{span: 8}}><Form.Control size="sm" type="text" placeholder="tags..." id="Tags" onChange={HandleTags} /></Col>
        </Row>
      </Col>
      </Row>
      </Container>

    </>
  );
}

export default FormClients;
