import { createSlice } from "@reduxjs/toolkit";

const clientSlice = createSlice({
    name:"client",
    initialState: {
        CompanyName: "",
        CompanyAdresse: "",
        CompanyStreet: "",
        CompanyCity: "",
        CompanyState: "",
        CompanyZip: "",
        CompanyCountry: "",
        Gst: "",
        Vat: "",
        WebsiteLink: "",
        Phone: "",
        Mobile: "",
        Email: "",
        Tags: "",

    },
    reducers:{
        toAddCompanyName: (state, action) => {
            state.CompanyName = action.payload;
        },
        toAddCompanyAdresse: (state, action) => {
            state.CompanyAdresse = action.payload;
        },
        toAddCompanyStreet:(state, action) => {
            state.CompanyStreet = action.payload;
        },
