            state.CompanyZip = action.payload; 
        },
        toAddCompanyCountry:(state,action) => { 
            state.CompanyCountry = action.payload; 
        },  
        toAddGst:(state,action) => { 
            state.Gst = action.payload;  
        },
        toAddVat:(state,action) => { 
            state.Vat = action.payload;
        },
        toAddWebsiteLink:(state,action) => {   
            state.WebsiteLink = action.payload; 
        }, 
        toAddWebsitePhone:(state,action) => { 
            state.Phone = action.payload;   
        },
        toAddMobile:(state,action) => {
            state.Mobile = action.payload;
        },
        toAddEmail:(state,action) => {
            state.Email = action.payload; 
        },
        toAddTags:(state,action) => {
            state.Tags = action.payload;
        }, 


            
    
            

        }
    }
)
export const {toAddCompanyName, toAddCompanyAdresse, toAddCompanyStreet, toAddCompanyCity, toAddCompanyState, toAddCompanyZip, toAddCompanyCountry, toAddGst, toAddVat, toAddWebsiteLink, toAddPhone, toAddTags, toAddMobile, toAddEmail } = clientSlice.actions
