import { createSlice } from "@reduxjs/toolkit";

const clientSlice = createSlice({
    name:"client",
    initialState: [],
    reducers:{
        toAddClient: (state, action) => {
            state.push ({
                CompanyName: action.payload.CompanyName,
                CompanyAdresse: action.payload.CompanyAdresse,
                CompanyStreet: action.payload.CompanyStreet,
                CompanyCity: action.payload.CompanyCity,
                CompanyState: action.payload.CompanyState,
                CompanyZip: action.payload.CompanyZip,
                CompanyCountry: action.payload.CompanyCountry,
                Gst: action.payload.Gst,
                Vat: action.payload.Vat,
                WebsiteLink: action.payload.WebsiteLink,
                Phone: action.payload.Phone,
                Mobile: action.payload.Mobile,
                Email: action.payload.Email,
                Tags: action.payload.Tags,

            })
    
            },

        }
    }
)
export const toAddClient = clientSlice.actions
export default clientSlice.reducer
