      vente_link4: "analyse",
    },
    {
      comptabilité_link1: "tableau de bord",
      comptabilité_link2: "vente",
      comptabilité_link3: "achat",
      comptabilité_link4: "rapport",
    },
    {
      gp_link1: "bulletins de l'employé",
      gp_link2: "lot de bulletins de paie",
    
      
      
    },
    {
      employé_link1: "employés",
      employé_link2: "contrats",
      employé_link3: "départmements",
    
      
      
    },

    {
      contacts_link1: "contacts",
    },
    {
      crm_link1: "vente",
      crm_link2: "pistes",
      crml_link3: "analyse",

    },
  ];

    return (
           <nav className="PlatformNav"> 
                <div className='PlatformNavLogo'>
                <a>GoMyErp</a>
                </div> 
                <div>
                   <ul className='PlatformNav-links'>
                        <li><a>A propos</a></li>
                        <li><a>Solution</a></li>
                        <li><a>Tarifs</a></li>
                    </ul>
                </div>
            </nav>
    );
}

export default PlatformNavbar;
