import { createSlice } from "@reduxjs/toolkit";

const moduleSlice = createSlice({
    name:"mod",
    initialState: {
        modules: " "
    },
    reducers:{
        change: (state, action) => {
            state.modules = action.payload
    
            }

        }
    }
)
export const {change} = moduleSlice.actions
export default moduleSlice.reducer
